<script>
import {isMobile,isTablet,isDesktop} from '@/js/breakpoints.js';

export default {
    name: "HomeSection",
    data(){
        return{
            isMobile: true,
            isTablet: false,
            isDesktop: false
        }
    },
    methods:{
        checkDevice(){
            if(isMobile()){
                this.isMobile = true;
                this.isTablet = false;
                this.isDesktop = false;
            }
            else if(isTablet()){
                this.isMobile = false;
                this.isTablet = true;
                this.isDesktop = false;
            }
            else if(isDesktop()){
                this.isMobile = false;
                this.isTablet = false;
                this.isDesktop = true;
            }
            else console.log('could not determine the device inside the checkDevice function of the Navbar component');
        },
    },
    mounted(){
        this.checkDevice();
        window.addEventListener("resize",this.checkDevice.bind(this));
        

        let resizeFunction = function(){
            let dividerLine = this.$el.querySelector('.divider-line rect');
            let {width} = dividerLine.getBoundingClientRect();

            let iconBox = this.$el.querySelector('.icon-box');
            iconBox.style.width = `${width}px`;
        }.bind(this)
        resizeFunction();
        window.addEventListener("resize",resizeFunction)
    }
}
</script>

<style lang="scss" scoped>
@use '@/assets/scss/setting' as *;

.home-section{
    position: relative;
    min-height: calc(100vh - var(--nav-bar-height));
    background-color: $primary-white;

    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
}

h1{
    text-align: center;
}
h2{
    text-align: center;
    margin-top: var(--spacing-top);
    margin-bottom: var(--spacing-normal);
}

.divider-line{
    margin: var(--spacing-large);
    width: 90vw;
}

.icon-box{
    display: flex;
    flex-direction: column;
    align-items: center;

    row-gap: var(--spacing-large);

    @include for-desktop-up{
        flex-direction: row;
        row-gap: none;

        justify-content: space-around;
    }
}




.box{
    display: flex;
    flex-direction: row;
    justify-content: flex-start;
    align-items: center;

    width: 80vw;
    padding: var(--spacing-normal);
    column-gap: var(--spacing-large);
    
    background: $primary-black;
    svg{
        fill: $primary-white;
    }
    color: $primary-white;

    @include for-desktop-up{
        width: auto;
        padding: none;
        column-gap: none;

        background: $primary-white;
        svg{
            fill: $primary-black;
        }
    }


    border-radius: 5px;
    text-decoration: none;
}

.scroll-icon{
    position: absolute;
    bottom: 0;
    margin: 0 auto;
    margin-bottom: var(--spacing-normal);

    fill: $primary-black;
}

</style>